import { Component } from '@angular/core';

@Component({
  selector: 'app-welcome',
  templateUrl: './welcome.component.html',
  styleUrls: ['./welcome.component.css']
})
export class WelcomeComponent {

}
<div class="container">
  <div class="row">
    <div class="col-md-6 center offset-md-3">
      <h2 class="text-center mt-5">Bienvenido a ChatHUB</h2>
      <div class="text-center mb-5">Únase a la sala para comenzar a chatear</div>
      <div class="card my-5">
        <button routerLink="/join-room" class="btn w-100 text-center">Únase a la sala ahora</button>
      </div>
    </div>
  </div>
</div>
