import { Component, OnInit, inject } from '@angular/core';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';
import { Router } from '@angular/router';
import { ChatService } from '../chat.service';

@Component({
  selector: 'app-join-room',
  templateUrl: './join-room.component.html',
  styleUrls: ['./join-room.component.css']
})
export class JoinRoomComponent implements OnInit {

  joinRoomForm!: FormGroup;
  fb = inject(FormBuilder);
  router = inject(Router);
  chatService = inject(ChatService);

  ngOnInit(): void {
    this.joinRoomForm = this.fb.group({
      user: ['', Validators.required],
      room: ['', Validators.required]
    });
  }

  joinRoom(){
    const {user, room} = this.joinRoomForm.value;
    sessionStorage.setItem("user", user);
    sessionStorage.setItem("room", room);
    this.chatService.joinRoom(user, room)
    .then(()=>{
      this.router.navigate(['chat']);
    }).catch((err)=>{
      console.log(err);
    })

  }

}
<div class="container">
  <div class="row">
    <div class="col-md-6 offset-md-3">
      <h2 class="text-center mt-5">Bienvenido a ChatHUB</h2>
      <div class="text-center mb-5">Únase a la sala para comenzar a chatear</div>
      <div class="card my-5">

        <form [formGroup]="joinRoomForm" class="card-body cardbody-color p-lg-5">

          <div class="text-center">
            <img src="https://cdn.pixabay.com/photo/2023/07/24/23/30/chat-8147986_1280.jpg"
              class="img-fluid profile-image-pic img-thumbnail rounded-circle my-3" width="200px" alt="profile">
          </div>

          <div class="mb-3">
            <input formControlName="user" type="text" class="form-control" id="displayname" aria-describedby="emailHelp"
              placeholder="Nombre de usuario">
          </div>
          <div class="mb-3">
            <input formControlName="room" type="text" class="form-control" id="roomname" placeholder="Nombre de la sala">
          </div>
          <div class="text-center">
            <button [disabled]="joinRoomForm.invalid" (click)="joinRoom()" type="submit" class="btn px-5 mb-5 w-100">Unirse</button>
          </div>
        </form>
      </div>

    </div>
  </div>
</div>
