import { AfterViewChecked, Component, ElementRef, OnInit, ViewChild, inject } from '@angular/core';
import { ChatService } from '../chat.service';
import { Router } from '@angular/router';

@Component({
  selector: 'app-chat',
  templateUrl: './chat.component.html',
  styleUrls: ['./chat.component.css']
})
export class ChatComponent implements OnInit, AfterViewChecked {


  chatService = inject(ChatService);
  inputMessage = "";
  messages: any[] = [];
  router = inject(Router);
  loggedInUserName = sessionStorage.getItem("user");
  roomName = sessionStorage.getItem("room");

  @ViewChild('scrollMe') private scrollContainer!: ElementRef;


  ngOnInit(): void {
    this.chatService.messages$.subscribe(res=>{
      this.messages = res;
      console.log(this.messages)
    });

    this.chatService.connectedUsers$.subscribe(res=>{
      console.log(res);

    })
  }

  ngAfterViewChecked(): void {
    this.scrollContainer.nativeElement.scrollTop = this.scrollContainer.nativeElement.scrollHeight;
  }

  sendMessage(){
    this.chatService.sendMessage(this.inputMessage)
    .then(()=>{
      this.inputMessage = '';
    }).catch((err)=>{
      console.log(err);
    })
  }

  leaveChat(){
    this.chatService.leaveChat()
    .then(()=>{
      this.router.navigate(['welcome']);
      setTimeout(() => {
        location.reload();
      }, 0);
    }).catch((err)=>{
      console.log(err);
    })
  }
}
<div class="container mt-5">

  <div class="chat_header">
    <h3 class="text-center my-2">Welcome to <span class="text-warning">{{roomName}}</span> Group</h3>
    <button (click)="leaveChat()" class="btn btn-danger">Leave Chat</button>
  </div>
  <div class="messaging">
    <div class="inbox_msg">
      <div class="inbox_people">
        <div class="headind_srch">
          <div class="recent_heading">
            <h4 class="text-white">Connected Users</h4>
          </div>
          <div class="srch_bar">
            <div class="stylish-input-group">
              <input type="text" class="search-bar" placeholder="Search">
              <span class="input-group-addon">
                <button type="button"> <i class="fa fa-search" aria-hidden="true"></i> </button>
              </span>
            </div>
          </div>
        </div>
        <div class="inbox_chat">
          <ng-container *ngFor="let user of this.chatService.connectedUsers$ | async">

            <div class="chat_list">
              <div class="chat_people">
                <div class="chat_img"> <img src="https://ptetutorials.com/images/user-profile.png" alt="sunil"> </div>
                <div class="chat_ib mt-3">
                  <h5>{{user}} <span class="online"></span></h5>
                </div>
              </div>
            </div>
          </ng-container>
        </div>
      </div>
      <div class="mesgs">
        <div #scrollMe class="msg_history">
          <ng-container *ngFor="let message of this.chatService.messages$ | async">
            <div
              [ngClass]="{'incoming_msg': message.user != loggedInUserName, 'outgoing_msg': message.user === loggedInUserName}">
              <div *ngIf="message.user != loggedInUserName" class="incoming_msg_img">
                <img src="https://ptetutorials.com/images/user-profile.png">
              </div>
              <div *ngIf="message.user != loggedInUserName" class="received_msg">
                <div class="received_withd_msg">
                  <p> {{message.message}}</p>
                  <span class="time_date">{{message.user}} | {{message.messageTime | date: 'medium'}}</span>
                </div>
              </div>
              <div *ngIf="message.user === loggedInUserName" class="sent_msg">
                <p>{{message.message}}</p>
                <span class="time_date">{{message.user}} | {{message.messageTime | date: 'medium'}}</span>
              </div>
            </div>
          </ng-container>
        </div>
        <div class="type_msg">
          <div class="input_msg_write">
            <input (keydown.enter)="sendMessage()" [(ngModel)]="inputMessage" type="text" class="write_msg"
              placeholder="Type a message" />
            <button (click)="sendMessage()" class="msg_send_btn" type="button"><i class="fa fa-paper-plane-o"
                aria-hidden="true"></i></button>
          </div>
        </div>
      </div>
    </div>

  </div>

</div>
